/*** @dataProvider getHelperDataProvider*/public function testGetHelper($inputHelperName, $expectedClass) {$this->assertInstanceOf($expectedClass, $this->_model->getHelper($inputHelperName));}/*** Data provider to run test with several data sets.** @return void*/public function getHelperDataProvier() {return array('class name' => array('core/data', 'Mage_Core_Helper_Data'),'module name' => array('core', 'Mage_Core_Helper_Data'));}